import React from "react";
import { useNavigate } from "react-router-dom";
import { Header } from "../components/Header";
import { getAuthStatus } from "../utils/api";


export const MemberPage = () => {
  let navigate = useNavigate();
  const [loading, setLoading] = React.useState(true)

  React.useEffect(() => {
    getAuthStatus()
      .then(({data}) => {
        console.log(data)
        let user = data
        setTimeout(() => setLoading(false), 750)  // Just here for jokes; and to confirm loading feature is working correctly
      })
      .catch((err) => {
        navigate('../login');
        setLoading(false);
      })  
  })

  //display if getAuthStatus has completed fully
  let display = (
    <div className="member-page">
      <div> <Header user={user}/></div>
      <h1>Member Page</h1>
    </div>    
    )

  // Display is getAuthStatus is still verifying  
  if (loading) {
    display = (
    <div className="member-page-loading">
      <h1>Loading poste...</h1>
    </div>
    )
  }

  return display
};
