import React from "react";
import { useNavigate } from "react-router-dom";
import { Header } from "../components/Header";
import { getAuthStatus } from "../utils/api";


export const MemberPage = () => {
  let navigate = useNavigate();
  const [loading, setLoading] = React.useState(true)
  const [userData, setUserData] = React.useState({})

  getAuthStatus()
      .then(({data}) => {
        console.log(data);
        setTimeout(() => setLoading(false), 750)  // Just here for jokes; and to confirm loading feature is working correctly
        setUserData(data);
      })
      .catch((err) => {
        setLoading(false);
      })  

  React.useEffect(() => {
    if (!user) {
      navigate('../login');
    } 
    
  })

  //display if getAuthStatus has completed fully
  let display = (
    <div className="member-page">
      <div> <Header user={userData}/></div>
      <h1>Member Page</h1>
    </div>    
    )

  // Display is getAuthStatus is still verifying  
  if (loading) {
    display = (
    <div className="member-page-loading">
      <h1>Loading poste...</h1>
    </div>
    )
  }

  return display
};
